Bot.prototype.findDistance = function(pos1, pos2){
  var _this = bot;
  var findDistanceMap = _this.map.reverse();
  
  var es = new EasyStar.js();
  es.setGrid(findDistanceMap);
  es.setAcceptableTiles([' ']);
  es.findPath(pos1[0], pos1[1], pos2[0], pos2[1], function(path){_this.pathLength = path.length});
  es.calculate();
  return _this.pathLength;


}


Bot.prototype.pathfinder = function(pos1, pos2){
    var _this = bot;
    //console.log("pathfinder bot :" + bot);
    var pathFinderMap = _this.map.reverse();
    //console.log(pathFinderMap);
    
    var easystar = new EasyStar.js();
    easystar.setGrid(pathFinderMap);
    easystar.setAcceptableTiles([' ']);

    //console.log(pos1[0], pos1[1], pos2[0], pos2[1]);
    easystar.findPath(pos1[0], pos1[1], pos2[0], pos2[1], function( path ) {
      console.log("length of path: " + path.length);
      console.log("next spot!" + path[1].x + ", "+ path[1].y);
      if (path === null) {
        console.log("none found +(");
        _this.pathDir = "none";  
      }
      else {
        if(path[1].x === pos1[0]){
          if(path[1].y < pos1[1]){
            console.log("set to north");
            _this.pathDir = "north";
            return "yup";
          }
          else{
            console.log("set to south");
            _this.pathDir = "south";
            return "yup";
          }
        }
        else{
          if(path[1].x > pos1[0]){
            console.log("set to east");
            _this.pathDir = "east";
            return "yup";
          }
          else{
            console.log("set to west");
            _this.pathDir = "west";
            return "yup";
          }
         }
        }
    });
    console.log("woop");
    easystar.calculate();
    return _this.pathDir;
    };
module.exports = Bot;
